mapper.update:
UPDATE BOARD
SET TITLE = #{title}, CONTENT = #{content}, BOARD_DATE = SYSDATE
WHERE BOARD_NO = #{boardNo} AND STATUS = 'Y' AND CATEGORY = #{category}

UPDATE BOARD
SET STATUS = 'N'
WHERE BOARD_NO = #{boardNo} AND STATUS = 'Y' AND CATEGORY = #{category}

UPDATE BOARD
SET REF_NO = #{refNo}
WHERE BOARD_NO = #{boardNo} AND STATUS = 'Y' AND CATEGORY = #{category} AND BOARD_LEVEL = 1
