mapper.select:
	SELECT NVL(COUNT(*), 0)
	FROM BOARD
	 
	    
	         title like #{val}
	     
	    
	        AND content like #{val}
	    
	    
	        AND writer like #{val}

SELECT BOARD_NO, WRITER, CATEGORY, TITLE, CONTENT, BOARD_DATE, STATUS, BOARD_LEVEL, REF_NO, EMAIL, USER_NAME, NICK_NAME, PHONE, BIRTHDAY, GENDER, ENROLL_DATE FROM BOARD B JOIN MEMBER M ON (M.USER_NO = B.WRITER)
TITLE like #{val}
AND CONTENT like #{val}
AND NICK_NAME like #{val}
START WITH BOARD_LEVEL = 1 CONNECT BY NOCYCLE PRIOR BOARD_NO = REF_NO
ORDER SIBLINGS BY BOARD_NO DESC

mapper.update:
UPDATE BOARD SET
CONTENT = #{ content }, BOARD_DATE = SYSDATE
WHERE BOARD_NO = #{
board_no }

UPDATE MEMBER SET USER_TYPE_LEVEL = '3'
WHERE USER_NO =
#{ user_No }

mapper.insert:
INSERT INTO BOARD
VALUES (SEQ_BOARD_NO.NEXTVAL, #{ writer }, 2, #{ title }, #{ content
}, SYSDATE, 'Y', 2, #{ ref_no })

mapper.update:
UPDATE
BOARD_STATUS SET STATUS = 'Y'
WHERE BOARD_NO = #{ ref_no }

