mapper.update:
UPDATE MEMBER
SET BIRTHDAY=#{birthday}, PHONE=#{phone}, NICK_NAME=#{nick_name}
WHERE  USER_NO=#{user_no}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE NICK_NAME = #{nick_name}

SELECT CYBERMONEY
FROM member
WHERE USER_NO = #{user_no}

SELECT PASSWORD 
FROM MEMBER 
WHERE USER_NO = #{user_no}

mapper.update:
UPDATE MEMBER 
SET PASSWORD = #{password} 
WHERE USER_NO = #{user_no}
